  exports.VersionError = function(code, message) {
    this.name = 'VersionError';
    this.code = code;
    this.message = message;
    this.toString = function() {
      if ( ! code )
        return message;
      return '[' + code + '] ' + this.name + ': ' + message;
    }
  };
  //   UnknownError
  //   ConstraintError
  //   DataError
  //   TransactionInactiveError
  //   ReadOnlyError
