  exports.NotFoundError = function(code, message) {
    this.name = 'NotFoundError';
    this.code = code;
    this.message = message;
    this.toString = function() {
      if ( ! code )
        return message;
      return '[' + code + '] ' + this.name + ': ' + message;
    }
  };
